            function getRating(rating) {
                let default_p = "0%";
                if (!isNaN(rating)) {
                    let starPercentage = (parseFloat(rating) / 5) * 100
                    let roundedPercentage = `${Math.round(starPercentage/10)*10}%`
                    return roundedPercentage
                }
                return default_p;
            }

